Review these tests for quality. They're in a TypeScript/Jest project.

test('user test', () => {
  const db = new MockDB();
  const svc = new UserService(db);
  const result = svc.createUser('john', 'john@test.com');
  expect(result.id).toBeDefined();
  expect(result.name).toBe('john');
  const found = svc.getUser(result.id);
  expect(found.name).toBe('john');
  expect(found.email).toBe('john@test.com');
  svc.deleteUser(result.id);
  expect(svc.getUser(result.id)).toBeNull();
});
